package Corejava;

import java.util.Scanner;

public class AreaOfcircle_for 
{

	public static void main(String[] args) {
		// TODO Auto-generated method stub
          Scanner s1= new Scanner(System.in);
for(int i=1;i<=10;i++)
{
		final double pi=3.142;
		int r=s1.nextInt();
		double area=(pi*r*r);
		System.out.println(area);
}
	}
}
	
	


